catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var funcName = "nsw2 set change req site info: ";
    console.info(funcName + "we are here");

    var selectedSite = newValue;
    console.info(funcName + "selectedSite: " + JSON.stringify(selectedSite));

    // call clinet utility getSiteSvcsInUse
    var detailsAjax = new GlideAjax('x_g_sonc_dit_nsw22.NcditNsw2UtilsClient');
    detailsAjax.addParam('sysparm_name', 'getSiteSvcsInUse');
    detailsAjax.addParam('sysparm_siteRcdId', selectedSite);
    detailsAjax.getXML(callbackGetSiteDetails);

    function callbackGetSiteDetails(response) {
        var siteResp = response.responseXML.documentElement.getAttribute("answer");
        var site_data = JSON.parse(siteResp);
        console.info(funcName + "at callback for site data");
        //console.info("debug response:", response);
        console.info("site_data: ", site_data);

        // set site service data info
        g_form.setValue('current_site_services_in_use', site_data.svcsInUseDescription);
    }

}
