catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
	// set Select a network service category: req_network_service_category to 1, Guaranteed Service (Enterprise WAN)
	// if client selects redundancy HA yes
    console.info("nsw2: nsw2 set svc cat for ha yes - called with newValue = " + newValue);
    if (newValue == 'Yes' && oldValue != 'Yes') {
        g_form.setValue('req_network_service_category', '1' );
        console.info("nsw2: req_network_service_category set to 1");
    } else {
        g_form.clearValue('req_network_service_category');
    }

}
