catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	// when requested_for changes, set vrf options to pick from based on sys_user u_agency
	console.log("nsw2 set vrf options client script is called");
	console.info("nsw2: changed requested_for to " + newValue);
	var thisUser_sysId = newValue;
	console.info("nsw2: sys_id for vrf search is " + thisUser_sysId);

	// call to get list of applicable services.
	var detailsAjax = new GlideAjax('x_g_sonc_dit_nsw22.NcditNsw2UtilsClient');
	detailsAjax.addParam('sysparm_name', 'getVrfs4user');
	detailsAjax.addParam('sysUserId', thisUser_sysId);
	detailsAjax.getXML(callbackGetVrfDetails);
}

function callbackGetVrfDetails(response) {
	var vrfList = response.responseXML.documentElement.getAttribute("answer");
	var vrfs = JSON.parse(vrfList);
	console.info("nsw2: at callback for vrfs");
	//console.info("debug response:", response);
	console.info("vrfs: ", vrfs);

	// set services options in dropdown selection here.
	g_form.clearOptions('requested_vrf');
	g_form.addOption('requested_vrf', '', '-- None --');
	for (var iVrfList = 0; iVrfList < vrfs.length; iVrfList++) {
		g_form.addOption('requested_vrf', vrfs[iVrfList].vrf_name, vrfs[iVrfList].vrf_label);
	}
}
