catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    console.info("client script: nsw2 set wan svc change options called.");

    // call to get list of applicable services.
    var detailsAjax = new GlideAjax('x_g_sonc_dit_nsw22.NcditNsw2UtilsClient');
    detailsAjax.addParam('sysparm_name', 'getServiceListByHACat');
    detailsAjax.addParam('ha_cat', 'any'); // blank category will match any category
    detailsAjax.getXML(callbackGetSvcDetails);
}

function callbackGetSvcDetails(response) {
    var svcList = response.responseXML.documentElement.getAttribute("answer");
    var services = JSON.parse(svcList);
    console.info("nsw2: at callback for services");
    //console.info("debug response:", response);
    console.info("services: ", services);

    // set services options in dropdown selection here.
    g_form.clearOptions('requested_service_codes');
    g_form.addOption('requested_service_codes', '', '-- None --');
    for (var iSvcList = 0; iSvcList < services.svcList.length; iSvcList++) {
        g_form.addOption('requested_service_codes', services.svcList[iSvcList].svc_code, services.svcList[iSvcList].svc_title);
    }
}
