catalog_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below
	// when requested_for changes, set vrf options to pick from based on sys_user u_agency
	console.log("nsw2 set site options on load client script is called");
	var thisUser_sysId = g_form.getValue('requested_for');
	console.info("nsw2: on load requested_for sys_id user for site search is " + thisUser_sysId);

	// call to get list of applicable services.
	var detailsAjax = new GlideAjax('x_g_sonc_dit_nsw22.NcditNsw2UtilsClient');
	detailsAjax.addParam('sysparm_name', 'getSites4user');
	detailsAjax.addParam('sysUserId', thisUser_sysId);
	detailsAjax.getXML(callbackGetSiteDetails);
}

function callbackGetSiteDetails(response) {
   console.info("nsw2: at callback for sites");
   console.info("nsw2 response:", response);
	var siteList = response.responseXML.documentElement.getAttribute("answer");
	var sites = JSON.parse(siteList);

	//console.info("debug response:", response);
	console.info("sites: ", sites);

	// set services options in dropdown selection here.
	g_form.clearOptions('site_rcd_ref');
	g_form.addOption('site_rcd_ref', '', '-- None --');
	for (var iSiteList = 0; iSiteList < sites.length; iSiteList++) {
		g_form.addOption('site_rcd_ref', sites[iSiteList].site_sys_id, sites[iSiteList].site_label);
	}
}
