cmn_map_page.script:
var funcName = "mappage dit_nsw22_sitemap: ";
gs.info(funcName + "Called to create nspro site map");
var siteCount = 0;
var mappedCount = 0;
var gr = new GlideRecord("cmn_location");
//gr.addQuery('u_nsw_site', '!=', null);
//gr.addEncodedQuery('u_nsw_siteISNOTEMPTY^latitudeISNOTEMPTY');
gr.addEncodedQuery('cmn_location_sourceLIKEnspro');
gr.addActiveQuery();
gr.query();
while (gr.next()) {
	siteCount += 1;
	if(gr.latitude && gr.longitude){
		var item = map.addItem(gr);
		item.latitude = gr.getValue('latitude');
		item.longitude = gr.getValue('longitude');
		item.dialog_title = gr.getDisplayValue();
		item.icon = "https://maps.google.com/mapfiles/kml/pushpin/grn-pushpin.png";
		item.icon_width = "22";
		item.icon_height = "22";
		mappedCount += 1;
	}
}
gs.info(funcName + "Gathered " + siteCount + " sites, mapped " + mappedCount + " for display");
