sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
	var funcName = "nsw2 BR: nsw2 set prov rcd defaults for new: ";
	gs.info(funcName + " request type is using " + current.request_type);
	var reqType = "";
	var shortDescMsgText = "";
    if (current.request_type == '1') {
		reqType = "New";
	} else if (current.request_type == '2') {
		reqType = "Move";
	} else if (current.request_type == '3') {
		reqType = "Change";
	} else if (current.request_type == '4') {
		reqType = "Delete";
	} else {
		reqType = "Undefined";
	}
	if (reqType == "New") {
		current.short_description = "nspro: " + reqType + " request for " + current.site_name;
	}
	if (reqType == "Move") {
		shortDescMsgText = current.site_rcd_ref.site_name;
		current.short_description = "nspro: " + reqType + " request for " + shortDescMsgText;
		current.site_name = current.site_rcd_ref.name;
	}
	if (reqType == "Change") {
		shortDescMsgText = current.site_rcd_ref.site_name;
		current.short_description = "nspro: " + reqType + " request for " + shortDescMsgText;
		current.site_name = current.site_rcd_ref.name;
	}
	if (reqType == "Delete") {
		shortDescMsgText = current.site_rcd_ref.site_name;
		current.short_description = "nspro: " + reqType + " request for " + shortDescMsgText;
		current.site_name = current.site_rcd_ref.name;
	}

})(current, previous);
