sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var funcName = "nsw2 BR: set prov rcd to assigned: ";
	gs.info(funcName + "BR rule triggered for task " + current.number);

	// check type filed to see which checklist template we want to apply
	// request types: 1 = new, 2 = move, 3 = change, 4 = delete
	var clTemplateName = "";
	if (current.request_type == "1") {
		clTemplateName = "nsw2 new req template";
	} else if (current.request_type == "2") {
		clTemplateName = "nsw2 move req template";
	}  else if (current.request_type == "3") {
		clTemplateName = "nsw2 change req template";
	}  else if (current.request_type == "4") {
		clTemplateName = "nsw2 delete req template";
	}

	var nsw2utils = new NcditNsw2Utils();
	var checklistTemplateResp = nsw2utils.nsw2Util_getChecklistTemplate(clTemplateName);
	// for debug - see what we retruned here
	global.JSUtil.logObject(checklistTemplateResp, funcName + "checklist template: ");
	if (checklistTemplateResp.status != "OK") {
		gs.warn(funcName + "Checklist not found for task " + current.number);
	} else {
		gs.info(funcName + "calling utility to generate proper checklist for task " + current.number + ", " + current.sys_id);
		nsw2utils.nsw2Util_genChecklistFromTemplate4Task(checklistTemplateResp.template, current.getTableName(), current.sys_id)
	}
	gs.info(funcName + "end of BR");

})(current, previous);
