sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	var thisLogPrefix = "nsw2SetProvStatesOnLoad: ";
	var reqType = g_form.getValue('request_type');  
	if (reqType == '4'){
		// reove these options from choices for provisoning_state
		//console.log(thisLogPrefix + "removing options 3,4,6,7,8,10");
		g_form.removeOption('provisoning_state', '3');
		g_form.removeOption('provisoning_state', '4');
		g_form.removeOption('provisoning_state', '6');
		g_form.removeOption('provisoning_state', '7');
		g_form.removeOption('provisoning_state', '8');
		g_form.removeOption('provisoning_state', '10');
	}
}
