sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var funcName = "nsw2 BR: set due date based on circ ord sent: ";
	var daysUntilDue = 90;
	gs.info(funcName + "called. Using daysUntilDue of " + daysUntilDue + " days.");
	gs.info(funcName + ", " +
			"current cir ord sent = " + current.circuit_order_sent_date.toString() +
			", previous cir ord sent = " + previous.circuit_order_sent_date.toString() +
			", current cir ord sent 2 = " + current.circuit_order_sent_date_2.toString() +
			", previous cir ord sent 2 = " + previous.circuit_order_sent_date_2.toString() );

	var newDueDate = new GlideDateTime(current.circuit_order_sent_date);
	// see what value changed
	if (current.circuit_order_sent_date != previous.circuit_order_sent_date) {
		newDueDate.addDaysLocalTime(daysUntilDue);
		current.due_date = newDueDate;
	} else {
		newDueDate = new GlideDateTime(current.circuit_order_sent_date_2);
		newDueDate.addDaysLocalTime(daysUntilDue);
		current.due_date = newDueDate;
	}
	current.update(); // 08/22/22 leave this for run after
	gs.info(funcName + "prov rcd " + current.number + " DUE DATE set to " + newDueDate.toString() + ". " + daysUntilDue + " days from circ ord sent.");

})(current, previous);
