sys_ui_action.script:
function createFSTaskWithInfo() {
    var thisFuncName = "createFSTaskWithInfo";
    jslog("Running " + thisFuncName);

    var promptBoxValue = prompt('Please enter a message you want to include for Field Service:');
    if (promptBoxValue) {
        jslog("user typed: " + promptBoxValue);
    } else {
        jslog("user skipped entry");
    }
    var PRSysId = g_form.getUniqueValue();
    jslog("Provisioning record sysid = " + PRSysId);

    // call to create FS task.
    var detailsAjax = new GlideAjax('x_g_sonc_dit_nsw22.NcditNsw2UtilsClient');
    detailsAjax.addParam('sysparm_name', 'reqCpeAllocationByFS');
    detailsAjax.addParam('sysparm_provRcdId', PRSysId);
    detailsAjax.addParam('sysparm_fs_msg', promptBoxValue);
    // detailsAjax.getXML(callbackTestfs);
    detailsAjax.getXMLAnswer(callbackTestfs);

    function callbackTestfs(response) {
        //console.log("debug response:", response);
        //var respRcdAnswer = response.responseXML.documentElement.getAttribute("answer");
        //jslog("debug respRcdAnswer:", respRcdAnswer);
        //var respInfo = JSON.parse(respRcdAnswer);
        var respInfo = JSON.parse(response); // better, shorter way because we used "getXMLAnswer" instead of just "getXML"
        jslog("respInfo.message = " + respInfo.message);
    }
}
