sys_ui_action.script:
action.setRedirectURL(current);
current.update();
if (! current.isActionAborted()) {
	// call a script include to perform the work
	// Instantiate the nsw2 utilities
	var nsw2util = new NcditNsw2Utils();
	var createRcdsResp = nsw2util.nsw2Util_createSiteAndLoc(current);
	if (createRcdsResp.status == 'OK') {
		gs.addInfoMessage("nsw2 server side script created site " + createRcdsResp.siteId + ", " + createRcdsResp.siteRcdId);
		// current rcd updates added 7/26/22 rw.
		current.work_notes = "New site record created for: " + createRcdsResp.siteId + ", Location sys_id: " + createRcdsResp.locRcdId;
		current.location = createRcdsResp.locRcdId;
		current.update();
	} else {
		gs.addErrorMessage("nsw2 server side script to created site and loc was called and returned " + createRcdsResp.status + " , " + createRcdsResp.message);
	}

	// redirect to here
    action.setRedirectURL(current);
}
