sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    // runs after data fetch from service def table
    // get all of the values and make a local table to reference on the page
    console.log("getLanWlanCpeCosts triggered to run after data fetch");
    console.log("we got " + api.data.look_up_records_1.results.length + " data records");
    var cpeCostsObject = {};
    for (var i = 0; i < api.data.look_up_records_1.results.length; i++) {
        console.log("data is :", api.data.look_up_records_1.results[i]);
        cpeCostsObject[api.data.look_up_records_1.results[i]['svc_code']['value']] = api.data.look_up_records_1.results[i]['cpm_cpe']['value'].toFixed(2);
    }
    console.log("got cpe values: ", JSON.stringify(cpeCostsObject));
    api.setState('cpeValues', JSON.stringify(cpeCostsObject));
    api.setState('LANB4', "$ " + cpeCostsObject['LANB4']);
    api.setState('LANB48', "$ " + cpeCostsObject['LANB48']);
    api.setState('LANB24', "$ " + cpeCostsObject['LANB24']);
    api.setState('LANP8', "$ " + cpeCostsObject['LANP8']);
    api.setState('LANP48', "$ " + cpeCostsObject['LANP48']);
    api.setState('LANP24', "$ " + cpeCostsObject['LANP24']);
    api.setState('WLANAP', "$ " + cpeCostsObject['WLANAP']);
    api.setState('wlanAp_num', cpeCostsObject['WLANAP']);
}
