sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    // called after telco rate look up data source has completed
    const funcName = "addTelcoRate";
    console.info(funcName + " has been called");

    console.log(funcName, JSON.stringify(event,null,4)); // for debug

    const tr_result = api.data.look_up_telcoRates.results[0];
    console.log(funcName, "tr_result", tr_result); // for debug

    var currentCost = api.state.qqCost;
    console.info("adding ", currentCost, " to telco cost ", tr_result.cost.value);
    currentCost = currentCost + tr_result.cost.value;

    var mcostStr = "$" + currentCost.toFixed(2);
    api.setState('qqMCostStr', mcostStr);


    return;
}
