sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    console.log("calcWlanMonthly triggered from input");
    const numberOfAPs = parseInt(event.payload.value);
    var monthlyTot = api.state.wlanAp_num * numberOfAPs;
    monthlyTot = monthlyTot.toFixed(2);
    api.setState('WLAN_MONTHLY_TOTAL', "$ " + monthlyTot);

}
