sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	// Implement email action here
	var gdt = new GlideDateTime();
	// expected fields
	logger.info("nsw2 telcoOrderResponse " + JSON.stringify(email.body));
	// we need to know if we are dealing with the primary or secondary circuit.
	// we can check the subject line to see if it contains the telco name
	// and use that to match which circuit entry set (1 = primary, 2 = secondary)
	if (email.subject.indexOf(current.telco_name) !== -1) {	
		// if primary
		current.circuit_ord_response_received_date = gdt;
		current.telco_order_contact = email.from;

		current.telco_response_message = email.body.comments;
		current.circuit_id = email.body.circuit_id;
		current.telco_r_order_number = email.body.telco_order_number;

		gd = new GlideDateTime();
		gd.setDisplayValue(email.body.circuit_due_date + " 00:00:00");
		current.circuit_due_date = gd;

		current.telco_r_monthly_cost = parseFloat(email.body.monthly_circuit_cost).toFixed(2);
		current.telco_r_constr_cost = parseFloat(email.body.construction_cost).toFixed(2);
		current.telco_r_total_cost = parseFloat(email.body.total_cost).toFixed(2);
		current.telco_r_other1time_cost = parseFloat(email.body.other_one_time_cost).toFixed(2);
	} else if (email.subject.indexOf(current.telco_name_2) !== -1) {
		// if secondary
		current.circuit_ord_response_received_date_2 = gdt;
		current.telco_order_contact_2 = email.from;

		current.telco_response_message_2 = email.body.comments;
		current.circuit_id_2 = email.body.circuit_id;
		current.telco_r_order_number_2 = email.body.telco_order_number;

		gd = new GlideDateTime();
		gd.setDisplayValue(email.body.circuit_due_date + " 00:00:00");
		current.circuit_due_date_2 = gd;

		current.telco_r_monthly_cost_2 = parseFloat(email.body.monthly_circuit_cost).toFixed(2);
		current.telco_r_constr_cost_2 = parseFloat(email.body.construction_cost).toFixed(2);
		current.telco_r_total_cost_2 = parseFloat(email.body.total_cost).toFixed(2);
		current.telco_r_other1time_cost_2 = parseFloat(email.body.other_one_time_cost).toFixed(2);
	} else {
		logger.error("nsw2 telcoOrderResponse No telco name match in reply; Subject: " + email.subject);
	}

	current.update();

})(current, event, email, logger, classifier);
